function App() {
  const data = { fileStr: "", type: "" }
  const [inputData, setInputData] = useState(data);
  const [countTotals, setCountTotal] = useState(0);

  const [nameLists, setNameList] = useState();
  const handleData = (e) => {
    setInputData({ ...inputData, [e.target.name]: e.target.value })
  }

  const handleSubmit = (e) => {

    e.preventDefault();
    Axios.post(url, inputData, {
      headers: {
        'Content-Type': 'application/json',
        'Access-Control-Allow-Origin': '*'
      }
    })
      .then((response) => {
        setCountTotal(response.data.countTotal);
        setNameList(response.data.nameList);
        var listName = response.data.nameList;
        console.log("name list: " + listName);


        console.log(response)
      })
  }

  var state = {
    // Initially, no file is selected
    selectedFile: null,
  };

  // On file select (from the pop up)
  var onFileChange = (event) => {
    // Update the state
    this.setState({
      selectedFile: event.target.files[0],
    });
  };

  // On file upload (click the upload button)
  var onFileUpload = () => {
    // Create an object of formData
    const formData = new FormData();

    // Update the formData object
    formData.append(
      "myFile",
      this.state.selectedFile,
      this.state.selectedFile.name
    );

    // Details of the uploaded file
    console.log(this.state.selectedFile);

    // Request made to the backend api
    // Send formData object
    Axios.post("api/uploadfile", formData);
  };

  // File content to be displayed after
  // file upload is complete
  var fileData = () => {
    if (this.state.selectedFile) {
      return (
        <div>
          <h2>File Details:</h2>
          <p>
            File Name:{" "}
            {this.state.selectedFile.name}
          </p>

          <p>
            File Type:{" "}
            {this.state.selectedFile.type}
          </p>

          <p>
            Last Modified:{" "}
            {this.state.selectedFile.lastModifiedDate.toDateString()}
          </p>
        </div>
      );
    } else {
      return (
        <div>
          <br />
          <h4>
            Choose before Pressing the Upload
            button
          </h4>
        </div>
      );
    }
  };

  return (
    // <div className="App">
    <div>
      <lable>Input Base64: </lable>
      <input type='text' name="fileStr" value={inputData.fileStr} onChange={handleData} /> <br></br>
      <button onClick={handleSubmit}>Submit</button><br></br>
      <br></br>
      <br></br>
      <label>Total Name: </label>
      <label>{countTotals}</label><br></br>
      <br></br>
      <br></br>
      <label>Name List: </label>
      {/* <table border="1">
        {nameLists.map(name =>
          <tr>
            <td>
              {name}
            </td>
          </tr>
        )}
      </table> */}
      <div>
        <input
          type="file"
          onChange={this.onFileChange}
        />
        <button onClick={this.onFileUpload}>
          Upload!
        </button>
      </div>
      {this.fileData()}
    </div>
  );
}

export default App;
